def get_ts_signature():
    # Extraído do seu utils.py (função gettsSignature)
    vec = {"vec": ""}
    try:
        req = requests.post('https://www.vavoo.tv/api/box/ping2', data=vec, timeout=20).json()
        return req['response'].get('signed')
    except:
        return None

def generate_m3u():
    vavoo_auth = get_ts_signature()
    if not vavoo_auth:
        print("Erro ao obter assinatura de stream.")
        return

    try:
        # Obter canais brutos
        r = requests.get("https://www2.vavoo.to/live2/index?output=json", timeout=20)
        channels = r.json()
        
        m3u = ["#EXTM3U\n"]
        for c in channels:
            # Lógica de URL do seu vjlive.py
            # Substitui vavoo-iptv por live2 e adiciona o token
            url = c['url'].replace("vavoo-iptv", "live2").split('.ts')[0]
            final_url = f"{url}.ts?n=1&b=5&vavoo_auth={vavoo_auth}"
            
            m3u.append(f'#EXTINF:-1 tvg-logo="{c.get("logo")}" group-title="{c.get("group")}",{c.get("name")}\n')
            m3u.append(f'{final_url}|User-Agent=VAVOO/2.6\n')

        with open("lista.m3u", "w", encoding="utf-8") as f:
            f.writelines(m3u)
        print("Lista gerada com sucesso!")
    except Exception as e:
        print(f"Erro: {e}")

if __name__ == "__main__":
    generate_m3u()
